svg.script:
// 等待SVG加载完成后执行
document.addEventListener('DOMContentLoaded', function() {
  const svg = document.getElementById('pig-svg');
  if (!svg) return; // 确保SVG元素存在
  
  const leftEye = document.getElementById('left-eye');
  const rightEye = document.getElementById('right-eye');
  if (!leftEye || !rightEye) return; // 确保眼睛元素存在
  
  // 计算SVG在页面中的位置（考虑滚动）
  function getSvgPosition() {
    const rect = svg.getBoundingClientRect();
    return {
      x: rect.left + window.scrollX,
      y: rect.top + window.scrollY,
      width: rect.width,
      height: rect.height
    };
  }
  
  // 鼠标移动事件
  svg.addEventListener('mousemove', function(e) {
    const pos = getSvgPosition();
    // 计算鼠标在SVG内部的相对坐标（0~300范围，对应viewBox）
    const mouseX = (e.clientX - pos.x) * (300 / pos.width);
    const mouseY = (e.clientY - pos.y) * (300 / pos.height);
    
    // 左眼跟踪逻辑
    const leftEyeBaseX = 135;
    const leftEyeBaseY = 95;
    const leftDeltaX = mouseX - leftEyeBaseX;
    const leftDeltaY = mouseY - leftEyeBaseY;
    const leftDistance = Math.sqrt(leftDeltaX * leftDeltaX + leftDeltaY * leftDeltaY);
    const leftMaxMove = 3; // 最大移动距离
    const leftMoveRatio = Math.min(leftMaxMove / leftDistance, 1);
    const leftNewX = leftDeltaX * leftMoveRatio;
    const leftNewY = leftDeltaY * leftMoveRatio;
    
    // 右眼跟踪逻辑
    const rightEyeBaseX = 165;
    const rightEyeBaseY = 95;
    const rightDeltaX = mouseX - rightEyeBaseX;
    const rightDeltaY = mouseY - rightEyeBaseY;
    const rightDistance = Math.sqrt(rightDeltaX * rightDeltaX + rightDeltaY * rightDeltaY);
    const rightMaxMove = 3; // 最大移动距离
    const rightMoveRatio = Math.min(rightMaxMove / rightDistance, 1);
    const rightNewX = rightDeltaX * rightMoveRatio;
    const rightNewY = rightDeltaY * rightMoveRatio;
    
    // 应用变换
    leftEye.setAttribute('transform', `translate(${leftNewX}, ${leftNewY})`);
    rightEye.setAttribute('transform', `translate(${rightNewX}, ${rightNewY})`);
  });
  
  // 鼠标离开时重置
  svg.addEventListener('mouseleave', function() {
    leftEye.setAttribute('transform', 'translate(0, 0)');
    rightEye.setAttribute('transform', 'translate(0, 0)');
  });
});
